import 'dotenv/config';
import express from 'express';
import expressConfig from './config/express';
import { connection } from './config/db';
import config from './config/setup';

const port = config.PORT || 8080;

const app = express();

expressConfig(app);

connection(app, port);

export default app;

import { json, urlencoded } from 'body-parser';
import cors from 'cors';
import router from '../router';
import { logger } from '../logger';
import { errorHandler } from '../../lib/http/lib.http.errorhandler';
import ApiResponse from '../../lib/http/lib.http.response';
import enums from '../../lib/enums';

const { MSG_ROUTE_DOES_NOT_EXIST, HTTP_NOT_FOUND } = enums;

const expressConfig = app => {
  logger.log('Application starting...', 'config.express.index.js');
  app.use(
    urlencoded({
      extended: true,
    }),
  );

  app.use(json());
  app.use(cors({
    origin: '*',
  }));

  router(app);

  app.get('/', (req, res) => {
    res.send({ message: 'Welcome' });
  });

  /** catch 404 and forward to error handler
   No routes matched? 404. */
  app.use((req, res /* next */) => ApiResponse.error(res, MSG_ROUTE_DOES_NOT_EXIST, HTTP_NOT_FOUND));
  app.use(errorHandler);
};

export default expressConfig;

import appVersion1 from './v1';

const router = (app) => {
  app.use('/api/v1', appVersion1);
};

export default router;

import { Router } from 'express';

const api = Router();

export default api;

import winston from 'winston';
import { Papertrail } from 'winston-papertrail';
import fse from 'fs-extra';
import config from '../setup';

const wslogger = winston.createLogger({
  transports: [
    new Papertrail({
      host: config.PAPERTRAIL_HOST,
      port: config.PAPERTRAIL_PORT,
    }),
  ],
});

const log = (value, type, location) => {
  if (config.DEBUG === 'true') {
    const formatMessage = `${new Date().toGMTString()} [${type}]: ${value}::${location}`;
    return formatMessage;
  }
  return '';
};

const logger = {
  info: (value, location = '') => {
    if (config.DEBUG === 'true') {
      const infoLog = './error.log';
      if (!fse.existsSync(infoLog)) {
        fse.writeFileSync(infoLog, 'utf-8');
      }
      const logs = fse.readFileSync(infoLog, 'utf-8');
      const logString = log(value, 'INFO', location);
      const write = `${logs}${logString} \n`;
      fse.outputFileSync(infoLog, write);
      wslogger.info(logString);
      if (config.NODE_ENV === 'development') {
        console.log(log(value, 'INFO', location));
      }
    }
  },
  error: (value, location = '') => {
    if (config.DEBUG === 'true') {
      const errorLog = './error.log';
      if (!fse.existsSync(errorLog)) {
        fse.writeFileSync(errorLog, 'utf-8');
      }
      const logs = fse.readFileSync(errorLog, 'utf-8');
      const logString = log(value, 'ERROR', location);
      const write = `${logs}${logString} \n`;
      fse.outputFileSync(errorLog, write);
      wslogger.error(logString);
      if (config.NODE_ENV === 'development') {
        console.log(log(value, 'ERROR', location));
      }
    }
  },
  log: (value, location = '') => {
    if (config.DEBUG === 'true') {
      const serverLog = './server.log';
      if (!fse.existsSync(serverLog)) {
        fse.writeFileSync(serverLog, 'utf-8');
      }
      const logs = fse.readFileSync(serverLog, 'utf-8');
      const logString = log(value, 'LOG', location);
      const write = `${logs}${logString} \n`;
      fse.outputFileSync(serverLog, write);
      wslogger.info(logString);
      if (config.NODE_ENV === 'development') {
        console.log(log(value, 'LOG', location));
      }
    }
  },
};

export { logger };

import {
  devENV, prodENV, testENV, stagingENV,
} from './env';

const { EIGHT_MEDICALS_NODE_ENV: NODE_ENV } = process.env;

export default {
  test: testENV,
  production: prodENV,
  development: devENV,
  staging: stagingENV,
}[NODE_ENV || 'development'];

import devENV from './development';
import prodENV from './production';
import stagingENV from './staging';
import testENV from './test';

export {
  devENV, prodENV, stagingENV, testENV,
};

import ApiResponse from './lib.http.response';
import enums from '../enums';
import { logger } from '../../config/logger';

const { MSG_ROUTE_DOES_NOT_EXIST, HTTP_NOT_FOUND } = enums;

// eslint-disable-next-line no-unused-vars
export const errorHandler = (error, req, res, next) => {
  const validCode = [ 200, 201, 400, 401, 403, 404, 409, 410, 422, 429, 554, 500, 503 ];
  let errorCode = error.code || 500;
  if (!validCode.includes(errorCode)) {
    errorCode = 500;
  }
  return ApiResponse.error(
    res,
    error,
    errorCode,
    error.label,
  );
};

export const catchErrors = fn => function(req, res, ...args) {
  return fn(req, res, ...args).catch(error => {
    logger.error(error, `${fn.name}`);
    res.status(400).json({
      status: false,
      errors: error.errors || error.message,
    });
  });
};

export const apiNotFound = (req, res) => ApiResponse.error(res, MSG_ROUTE_DOES_NOT_EXIST, HTTP_NOT_FOUND);

import status from 'http-status';
import enums from '../enums';
import { logger } from '../../config/logger';

const { SERVER_ERROR } = enums;

export default {
  success: (res, message, code, data) => res.status(code).json({
    status: 'success',
    message,
    code,
    data: data || [],
  }),

  error: (res, message = '', code = 500, label = '') => {
    const msg = code === 500 ? SERVER_ERROR : message;
    logger.log(`${message} - ${code} - ${label}`);
    return res.status(code).json({
      status: 'error',
      error: status[`${code}_NAME`],
      message: msg,
      code,
    });
  },
};

import * as labels from './lib.enums.labels';
import * as message from './lib.enums.message';
import * as status from './lib.enums.status';

export default {
  ...labels,
  ...message,
  ...status,
};

/* eslint-disable linebreak-style */
export const MSG_ROUTE_DOES_NOT_EXIST = 'Sorry that route/method does not exist';
export const RESOURCE_EXISTS = (resource) => `This ${resource} already exists`;
export const RESOURCE_NOT_EXIST = (resource) => `This ${resource} does not exist`;
export const RESOURCE_CREATED = (resource) => `${resource} created successfully`;

export const HTTP_OK = 200;
export const HTTP_CREATED = 201;
export const HTTP_UNAUTHORIZED = 401;
export const HTTP_FORBIDDEN = 403;
export const HTTP_BAD_REQUEST = 400;
export const HTTP_NOT_FOUND = 404;
export const HTTP_CONFLICT = 409;
export const HTTP_GONE = 410;
export const HTTP_TO_MANY_REQUESTS = 429;
export const HTTP_UNPROCESSABLE_ENTITY = 422;
export const HTTP_INTERNAL_SERVER_ERROR = 500;
export const SMTP_TRANSACTION_FAILED = 554;

import 'dotenv/config';
import pgp from 'pg-promise';
import promise from 'bluebird';
import detectPort from 'detect-port';
import config from '../setup';
import { logger } from '../logger';

const pg = pgp({ promiseLib: promise, noWarnings: true });
const db = pg(config.DATABASE_URL);

const connection = (app, port) => new Promise(resolve => {
  port = port || (detectPort());
  const server = app.listen(port, () => {
    logger.log(`Listening on port ${server.address().port}`, 'src.config.db::index.js');
    const originalClose = server.close.bind(server);
    server.close = () => new Promise(resolveClose => {
      originalClose(resolveClose);
    });
    db
      .connect()
      .then(conn => {
        logger.log(
          `connected to ${conn.client.database} database`,
          'src.config.db::index.js',
        );
      })
      .catch(err => {
        logger.log(err, 'src.config.db::index.js');
      });
  });
  resolve(server);
});

export { db, connection };
